INSERT INTO `users` VALUES (1,'Hoàng THị Hòa','hoanghoa11902@gmail.com','123456','Việt Nam','0373649173','nữ','https://giaitri.vn/wp-content/uploads/2019/11/hot-girl-Nam-%C4%90%E1%BB%8Bnh.jpg',NULL,1,'2023-06-09 14:27:31','2023-07-04 02:35:17','https://static.ohga.it/wp-content/uploads/sites/24/2020/02/lavoro-precario-convivere.jpg',NULL),(2,'Huỳnh Ngọc Phòng','chjkien91x@gmail.com','123456','Việt Nam','0384728445','nữ','https://nguoinoitieng.tv/images/nnt/97/0/bctu.jpg',NULL,1,'2023-06-10 01:51:26','2023-07-04 09:07:54','https://static.ohga.it/wp-content/uploads/sites/24/2020/02/lavoro-precario-convivere.jpg',NULL),(3,'Phạm Thị Hằng','chjkien99@gmail.com','123456','Việt Nam','0384728445','nữ','https://baogialai.com.vn/dataimages/201904/original/images2760019_1st.jpg',NULL,1,'2023-06-10 01:51:57','2023-07-04 09:06:58','https://static.ohga.it/wp-content/uploads/sites/24/2020/02/lavoro-precario-convivere.jpg',NULL),(4,'Trần thị Thuỳ Dung','chjkiena912@gmail.com','123456','Việt Nam','0384728445','nữ','https://image-us.24h.com.vn/upload/4-2018/images/2018-11-15/1542215723-230-hot-girl-tung-gay-nao-loan-pho-di-bo-phau-thuat-vi-khong-muon-goi-la-39mat-vuong39-a--1--1542182519-width550height550.jpg',NULL,1,'2023-06-18 09:16:27','2023-06-18 09:16:27','https://static.ohga.it/wp-content/uploads/sites/24/2020/02/lavoro-precario-convivere.jpg',NULL),(5,'Trần Huy Dũng','chjkien999@gmail.com','123456','Việt Nam','0384728445','nữ','https://vn-live-02.slatic.net/p/49c931dd11cde1e48fee9a07424a22dc.jpg',NULL,1,'2023-06-20 02:32:40','2023-07-19 14:38:12','https://static.ohga.it/wp-content/uploads/sites/24/2020/02/lavoro-precario-convivere.jpg','<p style=\"margin: 0.5em 0px; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">Phan Bội Châu sinh ngày<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/26_th%C3%A1ng_12\" title=\"26 tháng 12\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">26 tháng 12</a><span>&nbsp;</span>năm<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/1867\" title=\"1867\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">1867</a><span>&nbsp;</span>tại làng Đan Nhiễm, xã<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Xu%C3%A2n_H%C3%B2a,_Nam_%C4%90%C3%A0n\" title=\"Xuân Hòa, Nam Đàn\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Xuân Hòa</a>, huyện<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Nam_%C4%90%C3%A0n\" title=\"Nam Đàn\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Nam Đàn</a>, tỉnh<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Ngh%E1%BB%87_An\" title=\"Nghệ An\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Nghệ An</a>.</p><p style=\"margin: 0.5em 0px; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">Cha ông là Phan Văn Phổ, mẹ là Nguyễn Thị Nhàn. Ông nổi tiếng thông minh từ bé, năm 6 tuổi học 3 ngày thuộc hết<span>&nbsp;</span><i><a href=\"https://vi.wikipedia.org/wiki/Tam_t%E1%BB%B1_kinh\" title=\"Tam tự kinh\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Tam Tự Kinh</a></i>, 7 tuổi ông đã đọc hiểu sách<span>&nbsp;</span><i><a href=\"https://vi.wikipedia.org/wiki/Lu%E1%BA%ADn_ng%E1%BB%AF\" title=\"Luận ngữ\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Luận Ngữ</a></i>, 13 tuổi ông thi đỗ đầu huyện.</p><p style=\"margin: 0.5em 0px; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">Thuở thiếu thời ông đã sớm có lòng yêu nước. Năm 17 tuổi, ông viết bài<span>&nbsp;</span><i>\"Hịch Bình Tây Thu Bắc\"</i><span>&nbsp;</span>đem dán ở<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/%C4%90a\" title=\"Đa\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">cây đa</a><span>&nbsp;</span>đầu làng để hưởng ứng việc<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/B%E1%BA%AFc_K%E1%BB%B3\" title=\"Bắc Kỳ\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Bắc Kỳ</a><span>&nbsp;</span>khởi nghĩa kháng<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Ph%C3%A1p\" title=\"Pháp\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Pháp</a>. Năm 19 tuổi (<a href=\"https://vi.wikipedia.org/wiki/1885\" title=\"1885\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">1885</a>), ông cùng bạn là Trần Văn Lương lập đội \"Sĩ tử<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Phong_tr%C3%A0o_C%E1%BA%A7n_V%C6%B0%C6%A1ng\" title=\"Phong trào Cần Vương\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Cần Vương</a>\" (hơn 60 người) chống Pháp, nhưng bị đối phương kéo tới khủng bố nên phải giải tán.</p><p style=\"margin: 0.5em 0px; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">Gia cảnh khó khăn, ông đi dạy học kiếm sống và học thi. Khoa thi năm<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/%C4%90inh_D%E1%BA%ADu\" title=\"Đinh Dậu\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Đinh Dậu</a><span>&nbsp;</span>(1897) ông đã lọt vào trường nhì nhưng bạn ông là Trần Văn Lương đã cho vào tráp mấy cuốn sách nhưng ông không hề biết nên ông bị khép tội<span>&nbsp;</span><i>hoài hiệp văn tự</i><span>&nbsp;</span>(mang văn tự trong áo) nên bị kết án<span>&nbsp;</span><i>chung thân bất đắc ứng thí</i><span>&nbsp;</span>(suốt đời không được dự thi).<sup id=\"cite_ref-5\" class=\"reference\" style=\"line-height: 1em; font-size: 11.2px; white-space: nowrap; unicode-bidi: isolate; font-weight: normal; font-style: normal;\"><a href=\"https://vi.wikipedia.org/wiki/Phan_B%E1%BB%99i_Ch%C3%A2u#cite_note-5\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none; white-space: nowrap;\">[5]</a></sup></p><p style=\"margin: 0.5em 0px; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">Sau cái án này, Phan Bội Châu vào<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Hu%E1%BA%BF\" title=\"Huế\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Huế</a><span>&nbsp;</span>dạy học, do mến tài ông nên các quan đã xin vua<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Th%C3%A0nh_Th%C3%A1i\" title=\"Thành Thái\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Thành Thái</a><span>&nbsp;</span>xóa án. Nhờ vậy, ngay khoa<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Thi_h%C6%B0%C6%A1ng\" class=\"mw-redirect\" title=\"Thi hương\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">thi hương</a><span>&nbsp;</span>tiếp theo, năm<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Canh_T%C3%BD\" title=\"Canh Tý\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Canh Tý</a><span>&nbsp;</span>(<a href=\"https://vi.wikipedia.org/wiki/1900\" title=\"1900\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">1900</a>), ông đã đậu đầu (<a href=\"https://vi.wikipedia.org/wiki/Gi%E1%BA%A3i_nguy%C3%AAn\" title=\"Giải nguyên\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Giải nguyên</a>) ở trường thi<span>&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Ngh%E1%BB%87_An\" title=\"Nghệ An\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none;\">Nghệ An</a>.<sup id=\"cite_ref-6\" class=\"reference\" style=\"line-height: 1em; font-size: 11.2px; white-space: nowrap; unicode-bidi: isolate; font-weight: normal; font-style: normal;\"><a href=\"https://vi.wikipedia.org/wiki/Phan_B%E1%BB%99i_Ch%C3%A2u#cite_note-6\" style=\"text-decoration: none; color: rgb(6, 69, 173); background: none; white-space: nowrap;\">[6]</a></sup></p>'),(6,'Dương Thái Sơn','vana@gmail.com','123456','Hà Nội','0384728445','nữ','https://vn-live-02.slatic.net/p/49c931dd11cde1e48fee9a07424a22dc.jpg',NULL,1,'2023-07-02 07:48:23','2023-07-02 07:48:23','https://static.ohga.it/wp-content/uploads/sites/24/2020/02/lavoro-precario-convivere.jpg',NULL),(7,'Mai văn Thái','vanty@gmail.com','123456','Hà Nội','0384728445','nữ','https://vn-live-02.slatic.net/p/49c931dd11cde1e48fee9a07424a22dc.jpg',NULL,1,'2023-07-02 07:50:01','2023-07-02 07:50:01','https://static.ohga.it/wp-content/uploads/sites/24/2020/02/lavoro-precario-convivere.jpg',NULL),(10,'Thái Thị Hiền','hello2@gmail.com','123456','Hà Nội','0384728445','nữ','https://vn-live-02.slatic.net/p/49c931dd11cde1e48fee9a07424a22dc.jpg',NULL,1,'2023-07-02 08:00:09','2023-07-02 08:00:09','https://static.ohga.it/wp-content/uploads/sites/24/2020/02/lavoro-precario-convivere.jpg',NULL);
