INSERT INTO `works` VALUES (1,1,'Thực Tập Sinh Lập Trình Viên (Reactjs, React Native)','Hà Nội','<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d50997.11501757824!2d105.74992931536342!3d19.78161786741667!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3136f993f8ad6e37%3A0xdfa69bc720dd0a94!2sVinhomes%20Star%20city!5e1!3m2!1svi!2s!4v1624240285922!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\"></iframe>','vingroup@gmail.com','0963183435','21',' Cao đẳng','<p>quyền lợi</p>','2023-07-20','Full Time','<p>yêu cầu kinh nghiệm</p>','<p>mo ta</p>','<p>yêu cầu công việc</p>',1,'2023-06-12 08:28:20','2023-06-18 02:20:49',12,3),(2,2,'Thực tập sinh React Native','Nghệ An','<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d50997.11501757824!2d105.74992931536342!3d19.78161786741667!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3136f993f8ad6e37%3A0xdfa69bc720dd0a94!2sVinhomes%20Star%20city!5e1!3m2!1svi!2s!4v1624240285922!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\"></iframe>','chjkien9x@gmail.com','0963183435',NULL,'Đại học','<p>quyền lợi</p>','2023-07-16','Part Time','<p>yêu cầu kinh nghiệm</p>','<p>mo ta</p>','<p>yêu cầu công việc</p>',1,'2023-06-17 09:11:54','2023-06-18 02:24:16',23,10),(3,1,'Nhân Viên Marketing - Lương Trên 10 Triệu (Làm Việc Tại Gia Lâm, Hà Nội)','Hà Nội','<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d50997.11501757824!2d105.74992931536342!3d19.78161786741667!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3136f993f8ad6e37%3A0xdfa69bc720dd0a94!2sVinhomes%20Star%20city!5e1!3m2!1svi!2s!4v1624240285922!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\"></iframe>','chjkien9x@gmail.com','0963183435',NULL,'Cao đẳng','<p><span style=\"color: rgb(51, 51, 51); font-family: Roboto, sans-serif; font-size: 14px; font-style: normal; font-varian